package t09String;

public class Lab_09_StartAndEndwith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Aman";
		System.out.println("String value="+s);
		System.out.println("String starting with A= "+s.startsWith("A"));
		System.out.println("Ending with n = "+s.endsWith("n"));

	}

}
